import React, { useEffect, useState } from 'react'
import './Quiz.css'
import Button from 'react-bootstrap/Button';
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import Card from 'react-bootstrap/Card';



function Quiz() {

    const [allQuestions, setAllQuestions] = useState([])
    const [activeIndex, setActiveIndex] = useState(0)

    const getData = async () => {
        const { data } = await axios('db.json')
        // console.log(data.quiz);
        setAllQuestions(data.quiz)
    }
    // console.log(allQuestions);


    useEffect(() => {
        getData()
    }, [])

    const navigate = useNavigate()

    const nextClick = () => {

        if (activeIndex < allQuestions.length - 1) {
            setActiveIndex(activeIndex + 1)
        }
        else {

            navigate('/score')
        }
    }
    console.log(activeIndex);

    return (

        <div className='d-flex justify-content-center align-items-center quizPage' style={{ height: '100vh' }}>
            
            {allQuestions.length > 0 ? (
                <div className='container w-100 text-center p-5'>

                    <h3>{activeIndex+1+')'} {allQuestions[activeIndex].question}</h3>

                    <div className='d-flex justify-content-around mt-5'>

                        <div className='optionStyle text-center '>{allQuestions.options[0]}</div>
                        <div className='optionStyle text-center '>{allQuestions[1].options[1]}</div>
                    </div>

                    <div className='d-flex justify-content-around'>
                        <div className='optionStyle text-center   mt-3'>{allQuestions[2].options}</div>
                        <div className='optionStyle text-center  mt-3'>{allQuestions[3].options}</div>
                    </div>

                    <div className='d-flex justify-content-center mt-5'>
                        <Button onClick={nextClick} variant="success">Next</Button>
                    </div>

                </div>
            ) : (<h1>No Data</h1>)

            }

        </div>


    )
}

export default Quiz
