import React, { useEffect, useState } from 'react'
import './Quiz.css'
import Button from 'react-bootstrap/Button';
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import Card from 'react-bootstrap/Card';



function Quiz() {

    const [allQuestions,setAllQuestions]=useState([])
    const [activeIndex,setActiveIndex]=useState(0)

    const getData=async()=>{
        const {data}=await axios('db.json')
        // console.log(data.quiz);
        setAllQuestions(data.quiz)
    }
    // console.log(allQuestions);


    useEffect(()=>{
        getData()
    },[])

    const navigate=useNavigate()

    const nextClick=()=>{
       
       if(activeIndex < allQuestions.length-1){
        setActiveIndex(activeIndex+1)
       }
       else{

        navigate('/score')
       }
    }
    console.log(activeIndex);

  return (
    
        <div className='d-flex justify-content-center align-items-center quizPage' style={{height:'100vh'}}>
        <div className='container w-50 text-center p-5'>
            
              
                {/* //     <div>DOM stands for ?</div>

                //  <div className='d-flex justify-content-around mt-5'>
     
                //  <div className='optionStyle ps-5 pe-5'>1</div>
                //  <div className='optionStyle ps-5 pe-5'>2</div>
                //  </div>
     
                //  <div className='d-flex justify-content-around'>
                //  <div className='optionStyle ps-5 pe-5 mt-3'>3</div>
                //  <div className='optionStyle ps-5 pe-5 mt-3'>4</div>
                //  </div>
     
                //  <div className='d-flex justify-content-center mt-5'>
                //  <Button onClick={nextClick} variant="success">Next</Button>
                //  </div> */}

                
<Card className="container w-50 mt-5">
                {
                    allQuestions.length > 0 ? (
                        <Card.Body>

                            <Card.Text>

                                <ol className='ms-2 p-5 text-center' style={{ boxShadow: '0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19)', listStyle: 'none' }}>
                                    <li className='fs-4'>{`${activeIndex + 1} )-`}{allQuestions[activeIndex].question}</li>
                                </ol>

                                <div className='optionvalue p-3 text-center' style={{backgroundColor:buttonClick=="A"?(option=="correct")?'green':'red':("")}} id='A' onClick={(e) => scoreData((allQuestions[activeIndex].options[0]),e)}>{allQuestions[activeIndex].options[0]}</div>
                                <div id='B' style={{backgroundColor:buttonClick=="B"?(option=="correct")?'green':'red':("")}} onClick={(e) => scoreData((allQuestions[activeIndex].options[1]),e)} className='optionvalue p-3  mt-3 text-center'>{allQuestions[activeIndex].options[1]}</div>


                                <div id='C' style={{backgroundColor:buttonClick=="C"?(option=="correct")?'green':'red':("")}} onClick={(e) => scoreData((allQuestions[activeIndex].options[2]),e)} className='optionvalue p-3 mt-3 text-center' >{allQuestions[activeIndex].options[2]}</div>
                                <div id='D' style={{backgroundColor:buttonClick=="D"?(option=="correct")?'green':'red':("")}} onClick={(e) =>scoreData((allQuestions[activeIndex].options[3]),e)} className='optionvalue p-3 mt-3 text-center' >{allQuestions[activeIndex].options[3]}</div>

                            </Card.Text>

                            <div className='d-flex justify-content-center'>
                                <Button onClick={handleNext} className='mt-3' variant="primary" >Next</Button>
                            </div>


                        </Card.Body >

                    ) : (<h1>No Data</h1>)
                }

            </Card >

            
           
            
        </div>
    </div>
    
    
  )
}

export default Quiz
