import React, { useEffect, useState } from 'react'
import './Quiz.css'
import Button from 'react-bootstrap/Button';
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import Card from 'react-bootstrap/Card';



function Quiz() {

    const [allQuestions, setAllQuestions] = useState([])
    const [activeIndex, setActiveIndex] = useState(0)
    const [score,setScore]=useState(0)
    const [option,setOption]=useState('')
    const [buttonClick,setButtonClick]=useState('')

    const getData = async () => {
        const { data } = await axios('db.json')
        setAllQuestions(data.quiz)
    }


    useEffect(() => {
        getData()
    }, [])

    const navigate = useNavigate()

    const nextClick = () => {

        if (activeIndex < allQuestions.length - 1) {
            setActiveIndex(activeIndex + 1)
        }
        else {

            navigate('/score')
        }
    }

    const optionClick=(answer)=>{
        if(answer==allQuestions[activeIndex].answer){
            setScore(score+1)
            setOption('correct')

        }
        else{
            setOption('wrong')
        }

    }
    console.log(score);




    return (

        <div className='d-flex justify-content-center align-items-center quizPage' style={{ height: '100vh' }}>
            
            {allQuestions.length > 0 ? (
                <div className='container w-50 text-center p-5'>

                    <h3>{activeIndex+1+')'} {allQuestions[activeIndex].question}</h3>


                        <div onClick={()=>optionClick(allQuestions[activeIndex].options[0])} id='option1' className='optionStyle text-center p-3 mt-5'>{allQuestions[activeIndex].options[0]}</div>
                        <div onClick={()=>optionClick(allQuestions[activeIndex].options[1])} id='option2' className='optionStyle text-center p-3 mt-3'>{allQuestions[activeIndex].options[1]}</div>

                        <div onClick={()=>optionClick(allQuestions[activeIndex].options[2])} id='option3' className='optionStyle text-center p-3   mt-3'>{allQuestions[activeIndex].options[2]}</div>
                        <div onClick={()=>optionClick(allQuestions[activeIndex].options[3])} id='option4' className='optionStyle text-center p-3  mt-3'>{allQuestions[activeIndex].options[3]}</div>

                    <div className='d-flex justify-content-center mt-5'>
                        <Button onClick={nextClick} variant="success">Next</Button>
                    </div>

                </div>
            ) : (<h1>No Data</h1>)

            }

        </div>


    )
}

export default Quiz
